int chu(int a,int b)
{
	int tmp,count=1;
	if(a<b)
	{
		tmp=a;
		a=b;
		b=tmp;
	}
	count++;
	if(a%b==0)
	{
		return count;
	}
	else
	{
		return chu(b,a%b);
	}
}
int bei(int a,int b)
{
	int tmp,count=1;
	if(a<b)
	{
		tmp=a;
		a=b;
		b=tmp;
	}
	count++;
	if(a%b==0) {return count;}
	else
	{
		a+=a;
		return bei(a,b);
	}
}

