using namespace std;
int main()
{   
    cout<<"请输入一个正整数："；
    int i,j,N;
    double sum=0.00;
    cin>>N;
    double**p=new double*[N];
    for(i=0;i<N;i++)
    {
       for(j=0;j<N;j++)
       {
           p[i]=new double[j];
        }
     }
     for(i=0;i<N;i++)
    {
       for(j=0;j<N;j++)
       {
           p[i][j]=1/(N-i-1);
           sum+=p[i][j];
           cout<<p[i][j]<<'\t';
        }
        cout<<endl;
     }
     cout<<sum<<endl;
     return 0;
}

#include <stdio.h>
void ex(int &x,int &y)
{
	int p;
	if (x<y)
	{
		p=x;
		x=y;
		y=p;
	}
}
void main()
{
	int a,b,ans1=0,ans2=0;
	scanf("%d%d",&a,&b);
	ex(a,b);
	int i=a,j=b,k;
	while (j!=0)
	{
		k=i%j;
		i=j;
		j=k;
		ans1++;
	}
	i=a;
	while ((i%b)!=0)
	{
		i+=a;
		ans2++;
	}
	printf("%d %d\n",ans1,ans2);
}

