int func1(int put_a,int put_b)
{
	int remained;
	remained=put_a%put_b;
	int times=0;
	while(remained)
	{
		put_a=put_b;
		put_b=remained;
		remained=put_a%put_b;
		times++;
	}
	return times;
}

int func2(int put_a,int put_b)
{
	if (put_a>put_b)
	{
		int remained;
		remained=put_a%put_b;
		int ver=put_a;
		int times=1;
		while(remained)
		{
			remained=(put_a*ver)/put_b;	
			times++;
		}
		return times;
	}
	else
	{
		int remained;
		remained=put_b%put_a;
		int ver=put_b;
		int times=1;
		while(remained)
		{
			remained=(put_b*ver)/put_a;	
			times++;
		}
		return times;
	}
	
}

