void dfs(int x,int y)
{
	if(x-1>0&&c[x-1][y]=='@'&&flag[x-1][y]==0)
	{
		flag[x-1][y]=1;
		dfs(x-1,y);
	}
	if(y-1>0&&c[x][y-1]=='@'&&flag[x][y-1]==0)
	{
		flag[x][y-1]=1;
		dfs(x,y-1);
	}
	if(x+1<=n&&c[x+1][y]=='@'&&flag[x+1][y]==0)
	{
		flag[x+1][y]=1;
		dfs(x+1,y);
	}
	if(y+1<=m&&c[x][y+1]=='@'&&flag[x][y+1]==0)
	{
		flag[x][y+1]=1;
		dfs(x,y+1);
	}
}
