#include<iostream>
using namespace std;

int ZhanZhuan(int x, int y)
{
	int time = 0;
	
	while(true)
	{
		if( x < y)
		{
			int temp = x;
			x = y;
			y = temp;
		}

		if(x%y == 0)
			return time+1;
		x %= y;
		time++;
	}
}

int Palie(int x, int y)
{
	int time = 0;
	int tp_x ;
	if( x < y)
		{
			int temp = x;
			x = y;
			y = temp;
			tp_x = x;
		}
	else tp_x = x;
		
	while(true)
	{

		if(x%y == 0)
			return time;
		x += tp_x;
		time++;
	}
}
