int zhanzhuan (int a,int b)     //辗转相除法
{
	int c,num=0;
    if(a>b)
	{
		for(;c!=0;num++)
		{
			c=a%b;
			a=b;
			b=c;
		}
	}
	if(a<b)
	{
		for(;c!=0;num++)
		{
			c=b%a;
			b=a;
			a=c;
		}
	}


	return num;
}
int pailie (int a,int b)       //排列倍数法
{
	int c,i=0;
	if(a>b&&a%b!=0)
	{
		for(;c!=0;i++)
		{
			a+=a;
			c=a%b;
		}
	}
	if(a<b&&b%a!=0)
	{
		for(;c!=0;i++)
		{
			b+=b;
			c=b%a;
		}
	}
	

	return i;
}
