int time1(int a,int b);
int time2(int a,int b);
int main ()
{
	int a,b;
	cin>>a>>b;
    cout<<time1(a,b)<<" "<<time2(a,b)<<endl;
	return 0;
}
int time1(int a,int b)
{
	int c,t=0;
	
	if(a>b)
	{
        c=a-b;
	    if(b%c==0&&a%c==0)
		{
			t+=2;
		}
		else
		{
			a=b;
            b=c;
			t+=time1(a,b);
		}
	}else{
        c=b;
		b=a;
		a=c;
		t+=time1(a,b);
	}
	return t;
}
int time2(int a,int b)
{
    int t=0,c,d;
	if(a>b)
	{
		d=a;
		do{
            a+=d;  
			
			t++;
			t++;

		}while(a%b==0);
	
		
	}else{
		 c=b;
         b=a;
         a=c;
		 t+=time2(a,b);
	}
	return t;
}

