	{
		f=a;
		g=b;
	}
	else
	{
		f=b;
		g=a;
	}
	for(i=1;1;i++)
	{
		e=f%g;
	    f=g;
	    g=e;
		if(e==0)
		{
			break;
		}
	}
	return i;
} 
