{
	int e,i,f,g,h;
	if(c>d)
	{
		f=c;
		g=d;
	}
	else
	{
		f=d;
		g=c;
	}
	h=f;
	for(i=0;1;i++)
	{
		e=h%g;
		if(e==0)
		{
			break;
		}
		h+=f;
	}
	return i;
}

