using namespace std;
int gbs1(int a,int b)
{
	int temp,n=0;
	if(a<b)return gbs1(b,a);
	else
	while(b)
	{
		temp=a%b;
		a=b;
		b=temp;
		n++;
	}
}
		
int gbs2(int a,int b)
{
	int n=0;
	if(a==b)return n;
	if(a<b)return gbs2(b,a);
	int temp=a;
	if(a>b)
	{
        while(a%b)
		{
			a+=temp;
			n++;
		}
		return n;
	}
}
