	{
		temp=a;
		a=b;
		b=temp;
	}
	while(a%b!=0)
	{
		m++;
		a%=b;
		temp=b;
		b=a;
		a=temp;
	}
	return(m);
}
int pai(int a,int b)
{
	int i=1,n=0,temp;
	if(a<b)
	{
		temp=a;
		a=b;
		b=temp;
	}
	while(a%b!=0)
	{
		n++;
		i++;
		a*=i;
	}
	return(n);
}

