#include <stdio.h>

int gcc(int a, int b){
	int temp;

	if(b == 0)	return 0;
	if(a < b){
		temp = a;
		a = b;
		b = temp;
	}

	return 1 + gcc(b, a%b);
}

int method(int a, int b){
	int sum = 0;
	int temp = a;

	while(true){
		if(a % b == 0)	return sum;
		else{
			a += temp;
			sum++;
		}
	}
}

int main(){
	int a, b;

	scanf("%d%d", &a, &b);
	printf("%d %d\n", gcc(a, b), method(a, b));
}

