#include <stdio.h>

int gcc(int a, int b){
	int temp;

	if(b == 0)	return 0;
	if(a < b){
		temp = a;
		a = b;
		b = temp;
	}

	return 1 + gcc(b, a%b);
}

int method(int a, int b){
	int sum = 0;
	int temp = a;

	while(true){
		if(a % b == 0)	return sum;
		else{
			a += temp;
			sum++;
		}
	}
}

int main(){
	int a, b;

	scanf("%d%d", &a, &b);

	int m1 = method(a, b);
	int m2 = method(b, a);

	printf("%d %d\n", gcc(a, b), m1>m2?m2:m1);
}

