double sum(int n)
{
	double x=0,s;
	int m=n;
	for(int i=1;i<=n;i++)
	{
		for(int j=1;j<=m;j++)
		{
			x+=1.0/j;
		}
		m--;
	}
	s=2*x-n;
	return s;
}

