int abc(int c,int d,int*a)
{   
	if(c==S)
		sum++;
	if(c+d>S)
		if(i==j) return sum;else
			abc(a[--i],a[j],&a[0]);
		if(c+d==S)
			if(i==j) return sum;else
			{sum++;abc(a[--i],a[j],&a[0]);}
			if(c+d<S)
				if(i==j) return sum;else
				{b=c+d;abc(b,a[j+1],&a[0]);}
				return sum;
}
void px(int a1[],int N)
{   
	int ii,jj,th;
	for(ii=0;ii<N-1;ii++){
		for(jj=0;jj<N-ii-1;jj++){
			if(a1[jj]>a1[jj+1]){
				th=a1[jj+1];
				a1[jj+1]=a1[jj];
				a1[jj]=th;
			}
		}
	}
}

