{
	int H;
	cin>>H;
	double k=1,m=0;
	double sum=H;
	while((H-m)!=1){
		k++;
		m++;
		sum=sum+(1/k*(H-m)*2);
	}
	cout<<setiosflags(ios::fixed);
	cout<<setprecision(2)<<sum<<endl;
	int i,j;
	for(i=0;i<H;i++){
		for(j=0;j<H;j++){
			if(i<j){
				cout<<"1"<<"/"<<j+1-i<<" ";
			}
			else if(i==j){
				cout<<"1"<<"/"<<1<<" ";
			}
			else if(i>j){
				cout<<"1"<<"/"<<i+1-j<<" ";
			}
		}
		cout<<endl;}
	return 0;
}

