using namespace std;
int zzxc(int a,int b)
{
	int max,min,i=2;
	max=a>b?a:b;
    min=a<b?a:b;
	if(max%min!=0)
	{
		zzxc(min,max%min);
		i++;
	}
	return i-1;
}
int plbs(int a,int b)
{
	for(int k=2;;){
		int f=a*k;
		if(f%b==0)
			return k-1;
		else
			k++;
	}
}
int main()
{
	int a,b;
	cin>>a>>b;
	cout<<zzxc(a,b)<<" "<<plbs(a,b);
	cout<<"\n";
	return 0;
}

